mapper.select:
select id,name,level from role where 1=1
and id = #{roleId}
and name = #{roleName}
and level  >= #{startLevel}
and level  <= #{endLevel}

select id,user_id userId,name,job,gender,level,exp,last_login_time lastLoginTime,last_logout_time
lastLogoutTime,
add_time addTime,server_id serverId,isjinyan,isfenghao
from role where 1 = 1
and id = #{roleId}
and user_id = #{userId}
and name like concat('%',#{name},'%')
and level  <= #{endLevel}
and level  >= #{startLevel}

select id,level,name,last_upgrade_time lastUpgradeTime from role where 1 = 1
and name = #{roleName}
and last_upgrade_time  < 
    DATE_ADD(#{createDateEnd},INTERVAL 1 day)

and last_upgrade_time  >=  #{createDateStart}
order by level DESC

select id,name,power,history_max_power,history_min_power,last_upgrade_time lastUpgradeTime from role where 1 = 1
and name = #{roleName}
and last_upgrade_time  < 
    DATE_ADD(#{createDateEnd},INTERVAL 1 day)

and last_upgrade_time  >=  #{createDateStart}
order by power DESC
